{
  // ...
  "log_config": {
    "version": 1,
    "formatters": {
      "syslog_fmt": {
        "format": "%(name)s - %(levelname)s - %(message)s"
      }
    },
    "handlers": {
      // 其他 handler
      "syslog": {
         "class": "logging.handlers.SysLogHandler",
          "formatter": "syslog_fmt",
          // 可用上面任一 address
          "address": "/dev/log"
      }
    },
    "root": {
      "handlers": [
        // 其他 handler
        "syslog",

      ]
    }

  }
}
